import React from 'react';
import Axios from 'axios';
import './detail.css'
import {Link} from 'react-router-dom';
class Detail extends React.Component{
	constructor(props){
		super(props);
		this.state={
			info:{},
			visible:false,
			num:1
		}
	}
	componentDidMount(){
//		console.log(this.props.match.params.id)
		this.Fun()
	}
	
	Fun=()=>{
		Axios({
			method:'get',
			url:'http://shop.com/index.php/index/goods/detail',
			datatype:'json',
			params:this.props.match.params.id
		}).then((res)=>{
//			console.log(res)
			this.setState({
				info:res.data.data.info
			})
		})
	}
	buy=()=>{
		this.setState({
			visible:true
		})
	}
	hidden=()=>{
		this.setState({
			visible:false
		})
	}
	add=()=>{
		let a=this.state.num
		this.setState({
			num:++a
		})
	}
	cut=()=>{
		let a=this.state.num
		if(a<2){
			a=2
		}
		this.setState({
			num:--a
		})
	}
	render(){
		console.log(this.state.info.attr)
		return(
			<div>
				<div className="describe">
				<img alt="" src={this.state.info.img} />
					<div className="font05">{this.state.info.title}</div>
					<div className="font06">￥{this.state.info.price}</div>
					<div className="font07">订单满188包邮</div>
					<div onClick={this.buy} className="font08">立即购买</div>
				</div>
				{this.state.visible&&
					<div>
				<div className="tip"></div>
				<div className="goods">
					<div className="detail">
						<span onClick={this.hidden} className="mui-modal-close">X</span>
						<img alt="" src={this.state.info.attr[0].pic}/>
						<div className="product-hd">
							<div className="spu-title">豆瓣 丝巾上的美术馆</div>
							<div className="spu-price">￥178.00</div>
							<div className='spu-props-tips'>请选择颜色</div>
						</div>
						<div className="props-items">
							<div className="color">颜色 :</div>
								<div className="bd">
								{this.state.info.attr.map((val,index)=>{
									console.log(val)
									return(
										<span key={index} className='items'>11111{val.attrname}</span>
									)
								})}
									
								</div>
						</div>
						<div className="num">数量 :</div>
						<div className="mui-number-picker">
							<i onClick={this.cut}>-</i>
							<span>{this.state.num}</span>
							<i onClick={this.add}>+</i>
						</div>
						
						<div className='usebutton'>
							<Link to='/shop'><div className="fastbuy">加入购物车</div></Link>
							<div className="inshopcar">立即购买</div>
						</div>
					</div>
				</div>
				</div>
				}
			</div>
		)
	}
}

export default Detail;
