import React from 'react';
import './shop.css';

class Shop extends React.Component{
	constructor(){
		super();
		this.state={
			visible:false,
			othervisible:true,
			num:1
		}
	}
	hide=()=>{
		this.setState({
			visible:true
		})
	}
	othervisible=()=>{
		this.setState({
			othervisible:false
		})
	}
	no=()=>{
		this.setState({
			visible:false
		})
	}
	add=()=>{
	 	var a=this.state.num
	 	this.setState({
	 		num:++a
	 	})
	}
	cut=()=>{
		var a=this.state.num
		if(a<2){
			a=2
		}
		this.setState({
			num:--a
		})
	}
	render(){
		return(
			<div>
				<div className="only">
					<div className="top">豆瓣&nbsp;豆品</div>
					<div className="bigchecked">
						<input type="checkbox"></input>
						<span>豆瓣豆品</span>
					</div>
					<hr />
					{this.state.othervisible&&
					<div className="goodss">
						<div className="good">
							<input type="checkbox"></input>
							<img alt="" src="https://img3.doubanio.com/view/dianpu_product_item/small/public/p2049830.jpg" />
							<div className="smallgood">
								<div className="font-a">豆瓣丝巾上的美术馆</div>
								<div className="font-b">马蒂斯款</div>
								<i onClick={this.cut}>-</i>
								<span>{this.state.num}</span>
								<i onClick={this.add}>+</i>
							</div>
							<span onClick={this.hide} className="span-a">删除</span>
							<span className="span-b">￥178.00</span>
						</div>
						{this.state.visible&&
						<div className="tip02">
							<div className="next">
								<div onClick={this.no} className="quxiao">取消</div>
								<div onClick={this.othervisible} className="querenshanchu">确认删除</div>
							</div>
						</div>}
					</div>}
				</div>
			</div>
		)
	}
}

export default Shop;
