import React from 'react';
import Axios from 'axios';

class Detail extends React.Component{
	constructor(props){
		super(props);
		this.state={
			goods:[]
		}
	}
	componentDidMount(){
		console.log(this.props)
		this.Fun()
	}
	
	Fun=()=>{
		Axios.get("http://shop.com/index.php/index/goods/index")
		.then((res)=>{
			console.log(res)
			this.setState({
				goods:res.data.data.goods
			})
		})
	}
	render(){
		console.log(this.state.goods)
		return(
			<div>
				<img src="https://img1.doubanio.com/view/market_grip/raw/public/3f9e4d0a93d438a.jpg" />
				<div className="describe">
					<div className="font01">豆瓣 自然系列餐具</div>
					<div className="font02">￥138/4件套</div>
					
				</div>
			</div>
		)
	}
}

export default Detail;
