import React from 'react';
import './detail.css'
class Detail extends React.Component{
	constructor(props){
		super(props);
		this.state={
			goods:[]
		}
	}
	componentDidMount(){
		console.log(this.props)
	}
	
	render(){
		console.log(this.props)
		return(
			<div>
				<div className="describe">
				<img alt="" src={this.state.goods.img} />
					<div className="font05">{this.state.goods.title}</div>
					<div className="font06">￥{this.state.goods.price}</div>
					<div className="font07">订单满188包邮</div>
					<div className="font08">立即购买</div>
				</div>
			</div>
		)
	}
}

export default Detail;
