import React from 'react';
import './footer.css'
import Axios from 'axios';


class Footer extends React.Component{
	constructor(){
		super();
		this.state={
			goods:[]
		}
	}
	componentDidMount(){
		this.Fun()
	}

	Fun(){
		Axios.get("http://shop.com/index.php/index/goods/index")
		.then((res)=>{
			console.log(res)
			this.setState({
				goods:res.data.data.goods
			})
		})
	
	}
	render(){
		console.log(this.state.goods)
		return(
			<div>
				<div className="product-list">
					<div className="product-list-header">新品首发</div>
					<ul className="product-list-content">
						{this.state.goods.map((val,index)=>{
							console.log(val)
						})}
						<li className="product-list-item">
							<img src="https://img3.doubanio.com/img/files/file-1557373296-0.jpg" />
							<div className="product-card-info">
								<div className="font01">豆瓣自然餐具系列</div>
								<div className="font02">吃饭,是最重要的小事</div>
								<div className="product-card-bottom">
									<span className="font03">￥138</span>
									<span className="font04">新品上市</span>
								</div>
							</div>
						</li>
					</ul>
				</div>
			</div>
		)
	}
	
}


export default Footer;
