import React from 'react';
import './denglu.css';
import {Link} from 'react-router-dom';
import Axios from 'axios';


class Denglu extends React.Component{
	constructor(){
		super();
		this.state={
			InputValue:"",
			PassWord:""
		}
	}
	componentDidMount(){
		this.Fun()
	}
	getVal=(e)=>{
		this.setState({
			InputValue:e.target.value
		})
	}
	getPass=(e)=>{
		this.setState({
			PassWord:e.target.value
		})
	}
	Fun=()=>{
		Axios.post('http://shop.com/index.php/index/user/doLogin',
		{data:{
			phone:this.state.InputValue,
			password:this.state.PassWord
		}})
	}
	render(){
		return(
			<div>
				<div className="all">
					<span className="X">X</span>
					<div className="itemss">
						<div className="font-logo">登陆豆瓣</div>
						<span className="font-hui">登陆注册表示同意</span>&nbsp;&nbsp;
						<span className="font-green">豆瓣使用协议,隐私政策</span>
					</div>
					<div className="inputs">
						<input value={this.state.InputValue} onChange={this.getVal} placeholder="手机号" type="text" className="phone"></input>
						<input value={this.state.PassWord} onChange={this.getPass} placeholder="密码" type="text" className="password"></input>
					</div>
					<div className="button">登陆</div>
					<Link to="/zhuce"><div className="button">注册</div></Link>
				</div>
			</div>
		)
	}
}

export default Denglu;
