import React from 'react';
import Axios from 'axios';
import './detail.css'
class Detail extends React.Component{
	constructor(props){
		super(props);
		this.state={
			goods:[]
		}
	}
	componentDidMount(){
		console.log(this.props)
		this.Fun()
	}
	
	Fun=()=>{
		Axios.get("http://shop.com/index.php/index/goods/index")
		.then((res)=>{
			console.log(res)
			this.setState({
				goods:res.data.data.goods
			})
		})
	}
	render(){
		console.log(this.state.goods)
		return(
			<div>
				
				{this.state.goods.map((val,index)=>{
					return(
						<div key={index} className="describe">
						<img alt="" src={val.img} />
							<div className="font05">{val.title}</div>
							<div className="font06">￥{val.price}</div>
							<div className="font07">订单满188包邮</div>
							<div className="font08">立即购买</div>
						</div>
					)
				})}
			</div>
		)
	}
}

export default Detail;
