import React from 'react';
import './shop.css';
import Axios from 'axios';
class Shop extends React.Component{
	constructor(){
		super();
		this.state={
			visible:false,
			othervisible:true,
			num:1,
			cart:[],
			c1:true,
			c2:false,
			currentId:"",
			goodsArr:[]
			
		}
	}
	hide=(e)=>{
//		console.log(e.id)
		
		this.setState({
			currentId:e.id,
//			visible:true
		})
	}
	othervisible=(item)=>{
		console.log(item.id)
		let newGoods=this.state.cart
		newGoods.filter((ditem)=>{
			return(
				item.id !==ditem.id
			)
			console.log(newGoods)
			
		})
		this.setState({
//			othervisible:false
		})
	}
	no=()=>{
		this.setState({
			currentId:""
		})
	}
	add=()=>{
	 	var a=this.state.num
	 	this.setState({
	 		num:++a
	 	})
	}
	cut=()=>{
		var a=this.state.num
		if(a<2){
			a=2
		}
		this.setState({
			num:--a
		})
	}
	componentDidMount(){
		this.Fun()
	}
	Fun=()=>{
		const param=new URLSearchParams();
		const token=localStorage.getItem("token")
		param.append('token',token)
		
		Axios({
			method:'post',
			url:'http://shop.com/index.php/index/goods/cartinfo',
			data:param
		}).then((res)=>{
//			console.log(res)

			this.setState({
				cart:res.data.data.cart.map((result,list)=>{
					result.id=list
					return(
						result
					)
				})
			})
		})
		
	}
	render(){
//		console.log(this.state.currentId)
		return(
			<div>
				<div className="only">
					<div className="top">豆瓣&nbsp;豆品</div>
					<div className="bigchecked">
						<input type="checkbox"></input>
						<span>豆瓣豆品</span>
					</div>
					{this.state.cart.map((val,index)=>{
						return(
							<div key={index}>
							{this.state.othervisible&&
							<div className="goodss">
								<div className="good">
									<div className="c"></div>
									<img alt="" src={val.pic} />
									<div className="smallgood">
										<div className="font-a">{val.name}</div>
										<div className="font-b">{val.attr_name}</div>
										<b onClick={this.cut}>-</b>
										<span>{val.count}</span>
										<b onClick={this.add}>+</b>
									</div>
									<span onClick={this.hide.bind(this,val)} className="span-a">删除</span>
									<span className="span-b">￥{val.price}</span>
								</div>
								{this.state.currentId===val.id ? (
									<div className="tip02">
									<div className="next">
										<div onClick={this.no} className="quxiao">取消</div>
										<div onClick={this.othervisible.bind(this,val)} className="querenshanchu">确认删除</div>
									</div>
								</div>
								) : (
									null
								)
								}
							</div>}
							</div>
						)
					})}
				</div>
			</div>
		)
	}
}

export default Shop;
